<?phpnamespace Home\Controller;use Think\Controller;class CommonController extends Controller {    public function _initialize() {        $token   =   I('get.token');        if(!session('?store_id') && !empty($token))        {            $store_id=M('Store')->getFieldByToken($token,'id');                        M('Store')->where(array('id'=>  session('store_id')))->setField('token','');            if(!empty($store_id))            {                session('store_id',$store_id);                //代表商户管理员                session('syy_id',-1);            }        }        if (!session('?store_id') && CONTROLLER_NAME != 'Login') {            $this->redirect('Login/index');        }        $nav    = array(            'Manage'=>array(0=>'门店管理','index'=>'桌台管理','table'=>'桌台管理','orders'=>'订单管理','huizongday'=>'日汇总','huizongmonth'=>'月汇总'),                    );                $this->assign('nav',$nav);            }}