<?phpnamespace Home\Model;use Think\Model;class StoreModel extends Model{    protected  $_validate=array(        array('name','require','请填写门店名称！'),         array('name','','门店名称已经存在！',0,'unique',1),         array('area_id','require','请选择区域！'),    );    protected $_auto    = array(        array('addtime',NOW_TIME),        array('sid','session',self::MODEL_INSERT,'function','id') ,        array('enable_wifi','enables',self::MODEL_BOTH,'callback') ,        array('enable_card','enables',self::MODEL_BOTH,'callback') ,        array('enable_room','enables',self::MODEL_BOTH,'callback') ,        array('enable_park','enables',self::MODEL_BOTH,'callback') ,        array('enable_reservation','enables',self::MODEL_BOTH,'callback') ,        array('enable_queue','enables',self::MODEL_BOTH,'callback') ,    );      protected function enables($enable)    {        if(!empty($enable))            return 1;        return 0;    }}