<?phpnamespace Mobile\Controller;use Think\Controller;class TestController extends Controller {    public function apitools()    {        header("Content-type:text/html;charset=utf-8");                $sign=I('sign',1,'intval');        if(IS_POST)        {            $link=I('link');            $param=I('param');            $key=I('key');            if(!isset($_POST['sign']))            {                $sign=0;            }            $this->assign('link',$link);            $this->assign('key',$key);            $this->assign('param',$param);            foreach ($key as $kk=>$k)            {                if(!empty($k))                {                    $data[$k]=$param[$kk];                }            }            $url='diancan.kanuvip.com/api.php?'.$link;            if($sign)            $data['sign']=  $this->getSign($data);            echo '请求数组:<br/>';            dump($data);            $return = $this->cpost($url, $data);                        echo '返回json:<br/>'.$return.'<br />解析后:<br />';            dump(json_decode($return,true));                    }                $this->assign('sign',$sign);        $this->display();    }    private function cpost($url,$param,$post_file='')    {        $oCurl = curl_init();        if (stripos($url, "https://") !== FALSE) {            curl_setopt($oCurl, CURLOPT_SSL_VERIFYPEER, FALSE);            curl_setopt($oCurl, CURLOPT_SSL_VERIFYHOST, false);            curl_setopt($oCurl, CURLOPT_SSLVERSION, 1); //CURL_SSLVERSION_TLSv1        }        if (is_string($param) || $post_file) {            $strPOST = $param;        } else {            $aPOST = array();            foreach ($param as $key => $val) {                $aPOST[] = $key . "=" . urlencode($val);            }            $strPOST = join("&", $aPOST);        }        curl_setopt($oCurl, CURLOPT_URL, $url);        curl_setopt($oCurl, CURLOPT_RETURNTRANSFER, 1);        curl_setopt($oCurl, CURLOPT_POST, true);        curl_setopt($oCurl, CURLOPT_POSTFIELDS, $strPOST);        $sContent = curl_exec($oCurl);        $aStatus = curl_getinfo($oCurl);        curl_close($oCurl);        if (intval($aStatus["http_code"]) == 200) {            return $sContent;        } else {            return 'false';        }    }    private function getSign($data)    {        ksort($data);        foreach ($data as $k => $v) {            $v = urlencode($v);            $buff .= $k . "=" . $v . "&";        }        return md5($buff);    }}